-- note.comé¢¨ãƒ–ãƒ­ã‚°ã‚·ã‚¹ãƒ†ãƒ ã®ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹è¨­è¨ˆ

-- ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ†ãƒ¼ãƒ–ãƒ«
CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    password_hash VARCHAR(255) NOT NULL,
    display_name VARCHAR(100),
    bio TEXT,
    avatar_url VARCHAR(500),
    follower_count INTEGER DEFAULT 0,
    following_count INTEGER DEFAULT 0,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- è¨˜äº‹ãƒ†ãƒ¼ãƒ–ãƒ«
CREATE TABLE articles (
    id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users(id) ON DELETE CASCADE,
    title VARCHAR(200) NOT NULL,
    content TEXT NOT NULL,
    excerpt TEXT,
    slug VARCHAR(200) UNIQUE,
    status VARCHAR(20) DEFAULT 'draft', -- draft, published, archived
    view_count INTEGER DEFAULT 0,
    like_count INTEGER DEFAULT 0,
    comment_count INTEGER DEFAULT 0,
    published_at TIMESTAMP,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- ã‚¿ã‚°ãƒ†ãƒ¼ãƒ–ãƒ«
CREATE TABLE tags (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) UNIQUE NOT NULL,
    usage_count INTEGER DEFAULT 0,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- è¨˜äº‹ã‚¿ã‚°ä¸­é–“ãƒ†ãƒ¼ãƒ–ãƒ«
CREATE TABLE article_tags (
    article_id INTEGER REFERENCES articles(id) ON DELETE CASCADE,
    tag_id INTEGER REFERENCES tags(id) ON DELETE CASCADE,
    PRIMARY KEY (article_id, tag_id)
);

-- ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹ã®ä½œæˆ
CREATE INDEX idx_articles_user_id ON articles(user_id);
CREATE INDEX idx_articles_status ON articles(status);
CREATE INDEX idx_articles_published_at ON articles(published_at);
CREATE INDEX idx_article_tags_article_id ON article_tags(article_id);
CREATE INDEX idx_article_tags_tag_id ON article_tags(tag_id);

-- äººæ°—è¨˜äº‹ã®å–å¾—ã‚¯ã‚¨ãƒª
SELECT 
    a.id,
    a.title,
    a.excerpt,
    u.display_name,
    u.username,
    a.view_count,
    a.like_count,
    a.comment_count,
    a.published_at,
    ARRAY_AGG(t.name) as tags
FROM articles a
JOIN users u ON a.user_id = u.id
LEFT JOIN article_tags at ON a.id = at.article_id
LEFT JOIN tags t ON at.tag_id = t.id
WHERE a.status = 'published'
    AND a.published_at >= NOW() - INTERVAL '7 days'
GROUP BY a.id, u.id
ORDER BY (a.like_count * 2 + a.comment_count * 3 + a.view_count * 0.1) DESC
LIMIT 10;

-- ãƒ¦ãƒ¼ã‚¶ãƒ¼ã®çµ±è¨ˆæƒ…å ±æ›´æ–°
UPDATE users 
SET 
    follower_count = (
        SELECT COUNT(*) 
        FROM user_follows 
        WHERE following_id = users.id
    ),
    following_count = (
        SELECT COUNT(*) 
        FROM user_follows 
        WHERE follower_id = users.id
    )
WHERE id IN (
    SELECT DISTINCT user_id 
    FROM articles 
    WHERE updated_at >= NOW() - INTERVAL '1 hour'
);

-- æœˆæ¬¡ãƒ¬ãƒãƒ¼ãƒˆç”Ÿæˆã‚¯ã‚¨ãƒª
WITH monthly_stats AS (
    SELECT 
        DATE_TRUNC('month', published_at) as month,
        COUNT(*) as article_count,
        SUM(view_count) as total_views,
        SUM(like_count) as total_likes,
        AVG(view_count) as avg_views_per_article
    FROM articles 
    WHERE status = 'published'
    GROUP BY DATE_TRUNC('month', published_at)
)
SELECT 
    month,
    article_count,
    total_views,
    total_likes,
    avg_views_per_article,
    LAG(total_views) OVER (ORDER BY month) as prev_month_views,
    ROUND(
        (total_views - LAG(total_views) OVER (ORDER BY month)) * 100.0 / 
        NULLIF(LAG(total_views) OVER (ORDER BY month), 0), 2
    ) as view_growth_percent
FROM monthly_stats
ORDER BY month DESC;
